import React, { createContext, useReducer, useMemo, useContext } from "react";

export const themes= {
  light: {
    background: "white",
    font: "black",
  },
  dark: {
    background: "black",
    color: "white",
  }
};

export const initialState = { dentists: [], theme: "light", data: []}

export const ContextGlobal = createContext(initialState);

export const ContextProvider = ({ children }) => {
  //Aqui deberan implementar la logica propia del Context, utilizando el hook useMemo

  const [state, dispatch] = useReducer(appReducer, initialState);

  const contextValue = useMemo(() => ({ state, dispatch }), [state, dispatch]);



  return (
    <ContextGlobal.Provider value={{state, dispatch}}>
      {children}
    </ContextGlobal.Provider>
  );
};

const appReducer = (state, action) => {
  switch (action.type) {
    case 'TOGGLE_FAVORITE':
      const dentistId = action.payload;
      const isFavorite = state.favorites.includes(dentistId);

      return {
        ...state,
        favorites: isFavorite
          ? state.favorites.filter((id) => id !== dentistId)
          : [...state.favorites, dentistId],
      };
    default:
      return state;
  }
}
export const useContextGlobal = () => useContext(ContextProvider)
