import React, { usEffect, useState, useParams } from 'react'
import {  } from 'react-router-dom';
import axios from 'axios'

//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Detail = () => {

      const [dentist, setDentist] = useState()
      const {id }= useParams()
      const url = `http://localhost:3001/dentists/${id}`
      const favs = localStorage.getItem('favs') || []
              usEffect(() => {

              fetch(url)
              .then((res) => res.json())
              .then((data) => setDentist(data))
            }, [])

  const addFav = () => {
    localStorage.setItem('favs', dentist)
  }
  // Consumiendo el parametro dinamico de la URL deberan hacer un fetch a un user en especifico
  return (
    <div> 
          <h1>{dentist?.name} {dentist?.email} {dentist?.email} {dentist?.website}</h1>
          <img src="./images/doctor.jpg" alt="" />
          <br/>
          <button onClick={addFav}>Add Fav</button>
    </div>
    )
  }

export default Detail
