import React, { createContext, useReducer, useMemo, useContext, useEffect } from "react";


const appReducer = (state, action) => {
  switch (action.type) {
    case 'TOGGLE_FAVORITE':
      const dentistId = action.payload;
      const isFavorite = state.favorites.includes(dentistId);

      return {
        ...state,
        favorites: isFavorite
          ? state.favorites.filter((id) => id !== dentistId)
          : [...state.favorites, dentistId],
      };
    default:
      return state;
  }
}

