import React, { createContext, useContext, useState, useEffect} from "react";
import axios from "axios";

export const ContextGlobal = createContext()



export const useContextGlobal = () => useContext(ContextGlobal)

export const ContextProvider = ({ children }) => {

    const[dentists, setDentists] = useState([])
    const[favorites, setFavorites] = useState([])

    useEffect(() => {
      axios
      .get("http://localhost:3001/dentists")
        .then((res) => res.json())
        .then((data) => setDentists(data))  
      }, [])

    const appReducer = (state, action) => {
      switch (action.type) {
        case 'TOGGLE_FAVORITE':
          const dentistId = action.payload;
          const isFavorite = state.favorites.includes(dentistId);

          return {
            ...state,
            favorites: isFavorite
              ? state.favorites.filter((id) => id !== dentistId)
              : [...state.favorites, dentistId],
          };
        default:
          return state;
      }
    

    return (
      <ContextGlobal.Provider value={{dentists, setDentists}}>
        {children}
      </ContextGlobal.Provider>
    );
}
}

export default ContextProvider;
